INSERT INTO `protection` (`detail`) VALUES
('<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- รักษาความสะอาดของร่างกายอย่างสม่ำเสมอ</p></li><li><p>- หลีกเลี่ยงการใช้ของส่วนตัวร่วมกับผู้อื่น</p></li><li><p>- รักษาความสะอาดของสัตว์เลี้ยง</p></li><li><p>- สวมเสื้อผ้าที่แห้งและระบายอากาศได้ดี</p></li><li><p>- หลีกเลี่ยงการสัมผัสกับสัตว์หรือบุคคลที่ติดเชื้อ</p></li></ul>'),
('<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- ลดการบริโภคเกลือและอาหารที่มีไขมันสูง</p></li><li><p>- ควบคุมน้ำหนักให้อยู่ในเกณฑ์</p></li><li><p>- ออกกำลังกายอย่างสม่ำเสมอ</p></li><li><p>- งดสูบบุหรี่และลดการดื่มแอลกอฮอล์</p></li><li><p>- ลดความเครียดและนอนหลับให้เพียงพอ</p></li><li><p>- ตรวจวัดความดันโลหิตเป็นประจำ</p></li></ul>'),
('<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- ฉีดวัคซีนป้องกันอีสุกอีใสและวัคซีนป้องกันงูสวัดในผู้สูงอายุหรือกลุ่มเสี่ยง</p></li><li><p>- หลีกเลี่ยงการสัมผัสกับตุ่มน้ำของผู้ป่วยงูสวัด</p></li><li><p>- รักษาความสะอาดบริเวณผื่น งดเกาหรือแกะตุ่มน้ำ</p></li><li><p>- ดูแลสุขภาพให้แข็งแรงเพื่อเสริมภูมิคุ้มกัน</p></li></ul>'),
('<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- หลีกเลี่ยงการสัมผัสสารที่อาจแพ้ เช่น สบู่ แชมพู หรือสารเคมีบางชนิด</p></li><li><p>- รักษาความสะอาดของผิวหนังและรักษาความชุ่มชื้น</p></li><li><p>- ใส่เสื้อผ้าที่ไม่รัดแน่น ระบายอากาศได้ดี</p></li><li><p>- หลีกเลี่ยงการเกาจนผิวหนังเป็นแผล</p></li><li><p>- ปรึกษาแพทย์เมื่อผื่นคันไม่หาย หรือมีอาการรุนแรง</p></li></ul>'),
('<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- รักษาความสะอาดของผิวหนัง โดยเฉพาะบริเวณที่มีแผลหรือบาดเจ็บ</p></li><li><p>- หลีกเลี่ยงการเกาหรือบีบฝีด้วยมือสกปรก</p></li><li><p>- ใช้อุปกรณ์ส่วนตัว เช่น ผ้าเช็ดตัว ผ้าปูที่นอน ที่สะอาด</p></li><li><p>- รักษาแผลหรือบาดแผลให้สะอาดและป้องกันการติดเชื้อ</p></li><li><p>- หากมีแผลหรือฝีที่ไม่ดีขึ้น ควรพบแพทย์เพื่อรับการรักษาที่เหมาะสม</p></li></ul>'),
('<h3><strong>การป้องกัน :</strong></h3><ul><li><p>- หลีกเลี่ยงสิ่งกระตุ้นที่ทำให้เกิดลมพิษ เช่น อาหารบางชนิด ยา แมลงสัตว์ หรือความเครียด</p></li><li><p>- สวมเสื้อผ้าที่ระบายอากาศได้ดี</p></li><li><p>- งดการใช้ผลิตภัณฑ์ที่อาจระคายเคืองผิวหนัง</p></li><li><p>- หากทราบสาเหตุ ควรหลีกเลี่ยงอย่างเคร่งครัด</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน :</strong></p><ul><li><p>- พักผ่อนให้เพียงพอ</p></li><li><p>- ดื่มน้ำมากๆ</p></li><li><p>- รับประทานยาแก้ปวดลดไข้ (paracetamol)</p></li><li><p>- ทายา Calamine lotion เพื่อบรรเทาอาการคัน</p></li><li><p>- หลีกเลี่ยงการแกะเกาตุ่มเพื่อป้องกันการติดเชื้อแทรกซ้อนและรอยแผลเป็น</p></li><li><p>- กักตัวเพื่อป้องกันการแพร่เชื้อ</p></li><li><p>- วัคซีนป้องกันอีสุกอีใส</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน :</strong></p><ul><li><p>- พักผ่อนให้เพียงพอ ดื่มน้ำมากๆ</p></li><li><p>- รับประทานอาหารที่มีประโยชน์</p></li><li><p>- ล้างมือบ่อยๆ ด้วยสบู่หรือเจลแอลกอฮอล์</p></li><li><p>- สวมหน้ากากอนามัยเมื่ออยู่ในที่สาธารณะ</p></li><li><p>- หลีกเลี่ยงการสัมผัสใกล้ชิดกับผู้ป่วย</p></li><li><p>- ฉีดวัคซีนป้องกันไข้หวัดใหญ่ประจำปี</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน :</strong></p><ul><li><p>- รักษาความสะอาดบริเวณแผลด้วยน้ำสะอาดและสบู่อ่อน</p></li><li><p>- ป้องกันไม่ให้แผลถูกเกาหรือเปิดออก</p></li><li><p>- ใช้ผ้าปิดแผลหรือพลาสเตอร์ที่สะอาดเพื่อป้องกันการติดเชื้อ</p></li><li><p>- หากมีอาการอักเสบหรือหนอง ควรพบแพทย์เพื่อรับการรักษา</p></li><li><p>- หลีกเลี่ยงการใช้ของส่วนตัวร่วมกับผู้อื่น</p></li><li><p>- รักษาความชุ่มชื้นของผิวเพื่อป้องกันการเกิดแผลพุพองจากผิวแห้ง</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ป้องกันไม่ให้ยุงกัด เช่น ใช้มุ้ง ล้างแหล่งน้ำขังกำจัดลูกน้ำยุง</p></li><li><p>- ดื่มน้ำและพักผ่อนให้เพียงพอ</p></li><li><p>- หากมีไข้สูงและอาการรุนแรงควรพบแพทย์ทันที</p></li><li><p>- ไม่ควรใช้ยาที่ทำให้เลือดแข็งตัว เช่น แอสไพริน โดยไม่ได้รับคำแนะนำแพทย์</p></li><li><p>- ฉีดวัคซีนป้องกันไข้เลือดออกในกลุ่มที่มีความเสี่ยง </p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- รักษาความสะอาดบริเวณตาและมือ</p></li><li><p>- หลีกเลี่ยงการขยี้หรือเกาเปลือกตา</p></li><li><p>- ใช้ผ้าชุบน้ำอุ่นประคบบริเวณตากุ้งยิงวันละหลายครั้งเพื่อช่วยลดอาการบวมและเร่งให้ฝีแตก</p></li><li><p>- ไม่ใช้ของส่วนตัวร่วมกับผู้อื่น เช่น ผ้าเช็ดหน้า</p></li><li><p>- หากอาการรุนแรงหรือไม่ดีขึ้น ควรพบแพทย์เพื่อรับการรักษาเพิ่มเติม</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- หลีกเลี่ยงการสัมผัสแผลหรือของเหลวจากตุ่มน้ำของผู้ติดเชื้อ</p></li><li><p>- ใช้ถุงยางอนามัยเมื่อมีเพศสัมพันธ์เพื่อลดความเสี่ยง</p></li><li><p>- รักษาความสะอาดบริเวณที่เป็นแผล</p></li><li><p>- หลีกเลี่ยงการใช้ของใช้ส่วนตัวร่วมกับผู้อื่น</p></li><li><p>- พบแพทย์เพื่อรับยาต้านไวรัสเมื่อมีอาการหรือตามคำแนะนำ</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- หลีกเลี่ยงสารก่อภูมิแพ้ เช่น ฝุ่น เกสรดอกไม้ และขนสัตว์</p></li><li><p>- ทำความสะอาดบ้านและพื้นที่อยู่อาศัยอย่างสม่ำเสมอ</p></li><li><p>- ใช้เครื่องฟอกอากาศเพื่อลดสารก่อภูมิแพ้ในอากาศ</p></li><li><p>- ใช้น้ำเกลือล้างจมูกเพื่อลดอาการคัดจมูก</p></li><li><p>- ปรึกษาแพทย์เพื่อรับยารักษาและคำแนะนำเพิ่มเติม</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- พักผ่อนให้เพียงพอและดื่มน้ำมาก ๆ</p></li><li><p>- ใช้ผ้าชุบน้ำอุ่นประคบเพื่อลดอาการปวดและบวม</p></li><li><p>- รับประทานอาหารอ่อนหรือของเหลวเพื่อไม่ให้เจ็บคอมาก</p></li><li><p>- หลีกเลี่ยงการสัมผัสใกล้ชิดกับผู้อื่นในช่วงที่มีอาการ</p></li><li><p>- ฉีดวัคซีน MMR (วัคซีนป้องกันหัด คางทูม และหัดเยอรมัน) ตามคำแนะนำของแพทย์</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- พักผ่อนให้เพียงพอ</p></li><li><p>- ดื่มน้ำอุ่นหรือของเหลวมาก ๆ เพื่อบรรเทาอาการเจ็บคอ</p></li><li><p>- กลั้วคอด้วยน้ำเกลือเพื่อบรรเทาอาการอักเสบ</p></li><li><p>- หลีกเลี่ยงการใช้ของใช้ส่วนตัวร่วมกับผู้อื่น</p></li><li><p>- หากติดเชื้อแบคทีเรีย ควรรับประทานยาปฏิชีวนะตามแพทย์สั่งจนหมด</p></li><li><p>- หมั่นล้างมือและรักษาสุขอนามัยเพื่อลดโอกาสการติดเชื้อ</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ดื่มน้ำมาก ๆ เพื่อช่วยให้มูกไม่เหนียว</p></li><li><p>- ล้างจมูกด้วยน้ำเกลือเพื่อช่วยลดอาการคัดจมูก</p></li><li><p>- หลีกเลี่ยงควัน ฝุ่น และสารก่อภูมิแพ้</p></li><li><p>- รักษาโรคภูมิแพ้หรือหวัดให้หายเร็ว ไม่ปล่อยให้เรื้อรัง</p></li><li><p>- หากมีอาการรุนแรงหรือเป็นบ่อย ควรปรึกษาแพทย์เพื่อวินิจฉัยและรักษา</p></li><li><p>- ในบางรายอาจต้องใช้ยาปฏิชีวนะ หรือยาพ่นจมูกตามคำแนะนำแพทย์</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- แปรงฟันอย่างถูกวิธีอย่างน้อยวันละ 2 ครั้ง</p></li><li><p>- ใช้ไหมขัดฟันหรือน้ำยาบ้วนปากช่วยกำจัดคราบแบคทีเรีย</p></li><li><p>- หมั่นตรวจสุขภาพช่องปากกับทันตแพทย์ทุก 6 เดือน</p></li><li><p>- หลีกเลี่ยงอาหารที่มีน้ำตาลสูงซึ่งเพิ่มโอกาสเกิดคราบแบคทีเรีย</p></li><li><p>- งดสูบบุหรี่ และควบคุมโรคเบาหวานหากมี เพราะส่งผลต่อสุขภาพเหงือก</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- รับประทานอาหารที่มีไอโอดีนเพียงพอ เช่น เกลือเสริมไอโอดีน อาหารทะเล</p></li><li><p>- หมั่นตรวจสุขภาพโดยเฉพาะหากมีประวัติคอพอกในครอบครัว</p></li><li><p>- หลีกเลี่ยงสารที่อาจรบกวนการทำงานของต่อมไทรอยด์ เช่น ควันบุหรี่</p></li><li><p>- หากมีอาการผิดปกติควรปรึกษาแพทย์เพื่อตรวจเลือดและอัลตราซาวด์</p></li><li><p>- การรักษาอาจใช้ยา รังสี หรือการผ่าตัด ขึ้นอยู่กับสาเหตุและขนาดของก้อน</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ลุกขึ้นช้า ๆ จากท่านอนหรือท่านั่ง เพื่อป้องกันหน้ามืด</p></li><li><p>- ดื่มน้ำให้เพียงพออย่างน้อย 6–8 แก้วต่อวัน</p></li><li><p>- รับประทานอาหารให้ครบหมู่ และทานอาหารมื้อเล็ก ๆ บ่อยครั้ง</p></li><li><p>- หลีกเลี่ยงแอลกอฮอล์ และการยืนนาน ๆ โดยไม่ขยับตัว</p></li><li><p>- ใส่ถุงรัดขา (compression stockings) เพื่อช่วยให้เลือดไหลเวียนดีขึ้นในรายที่เป็นเรื้อรัง</p></li><li><p>- หากมีอาการบ่อยหรือรุนแรง ควรปรึกษาแพทย์เพื่อตรวจหาสาเหตุและรับการรักษา</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- พักผ่อนสายตาและหลีกเลี่ยงการขยี้ตาแรง ๆ</p></li><li><p>- หลีกเลี่ยงกิจกรรมที่เพิ่มแรงดันในร่างกาย เช่น เบ่งแรง จามหรือไอแรง</p></li><li><p>- หากมีโรคประจำตัว เช่น ความดันโลหิตสูง ควรควบคุมให้ดี</p></li><li><p>- หากเกิดซ้ำบ่อย ควรปรึกษาแพทย์เพื่อตรวจดูการแข็งตัวของเลือดหรือโรคอื่น ๆ ที่เกี่ยวข้อง</p></li><li><p>- โดยทั่วไปไม่จำเป็นต้องใช้ยา ยกเว้นมีอาการระคายเคือง ซึ่งอาจใช้ยาหยอดตาตามแพทย์แนะนำ</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ดื่มน้ำเกลือแร่ (ORS) เพื่อชดเชยการสูญเสียน้ำและเกลือแร่</p></li><li><p>- หลีกเลี่ยงอาหารมัน ของทอด อาหารดิบ และนมระหว่างมีอาการ</p></li><li><p>- พักผ่อนให้เพียงพอ และดูแลสุขอนามัยอย่างเคร่งครัด</p></li><li><p>- หมั่นล้างมือก่อนรับประทานอาหารและหลังเข้าห้องน้ำ</p></li><li><p>- รับประทานอาหารที่ปรุงสุก สะอาด และดื่มน้ำต้มสุกหรือน้ำกรอง</p></li><li><p>- หากมีไข้สูง ถ่ายเป็นเลือด หรืออาการไม่ดีขึ้นใน 2–3 วัน ควรพบแพทย์</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ดื่มน้ำเกลือแร่ (ORS) เพื่อทดแทนของเหลวและเกลือแร่ที่สูญเสียไป</p></li><li><p>- หลีกเลี่ยงการใช้ยาหยุดถ่ายโดยไม่จำเป็น เพราะอาจทำให้เชื้ออยู่ในลำไส้นานขึ้น</p></li><li><p>- รับประทานอาหารอ่อน ย่อยง่าย หลีกเลี่ยงของมัน ดิบ หรือเผ็ด</p></li><li><p>- ล้างมือให้สะอาดก่อนกินอาหารและหลังเข้าห้องน้ำ</p></li><li><p>- กินอาหารปรุงสุก และดื่มน้ำสะอาด (น้ำต้มสุกหรือน้ำกรอง)</p></li><li><p>- หากมีไข้สูง ถ่ายเป็นเลือด อ่อนเพลียมาก หรืออาการไม่ดีขึ้นใน 1–2 วัน ควรรีบพบแพทย์ เพราะอาจต้องใช้ยาปฏิชีวนะเฉพาะ</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ลดความเครียดด้วยการผ่อนคลาย เช่น การฝึกหายใจลึก ๆ หรือทำสมาธิ</p></li><li><p>- หลีกเลี่ยงเครื่องดื่มที่มีคาเฟอีน แอลกอฮอล์ และบุหรี่</p></li><li><p>- นอนหลับให้เพียงพอ และออกกำลังกายอย่างเหมาะสม</p></li><li><p>- ตรวจสุขภาพหัวใจอย่างสม่ำเสมอ โดยเฉพาะผู้ที่มีโรคประจำตัว เช่น ความดันโลหิตสูง เบาหวาน</p></li><li><p>- หากมีอาการบ่อยหรือรุนแรง ควรพบแพทย์เพื่อตรวจวินิจฉัยและรับการรักษา</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ปรุงอาหารโดยเฉพาะเนื้อสัตว์ให้สุกทั่วถึงก่อนรับประทาน</p></li><li><p>- รักษาความสะอาดของอาหารและน้ำดื่ม</p></li><li><p>- ล้างมือให้สะอาดก่อนรับประทานอาหารและหลังเข้าห้องน้ำ</p></li><li><p>- หากสงสัยว่าติดพยาธิ ควรพบแพทย์เพื่อตรวจวินิจฉัยและรับยาถ่ายพยาธิ</p></li><li><p>- ตรวจสุขภาพเป็นประจำโดยเฉพาะในกลุ่มเสี่ยง เช่น ผู้ที่อยู่ในพื้นที่ที่มีการระบาด</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- รักษาความสะอาดของมือให้ดี โดยล้างมือด้วยสบู่บ่อย ๆ โดยเฉพาะก่อนกินอาหารและหลังเข้าห้องน้ำ</p></li><li><p>- ตัดเล็บให้สั้นและสะอาด เพื่อลดการเกาและแพร่ไข่พยาธิ</p></li><li><p>- ซักผ้าปูที่นอน เสื้อผ้า และผ้าขนหนูด้วยน้ำร้อน เพื่อกำจัดไข่พยาธิ</p></li><li><p>- หลีกเลี่ยงการเกาบริเวณทวารหนัก</p></li><li><p>- รับประทานยาถ่ายพยาธิตามคำแนะนำของแพทย์ และอาจต้องรักษาทั้งครอบครัวพร้อมกันเพื่อลดการติดเชื้อซ้ำ</p></li><li><p>- ทำความสะอาดสิ่งของใช้ส่วนตัวและทำความสะอาดบ้านอย่างสม่ำเสมอ</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ดื่มน้ำมาก ๆ เพื่อช่วยล้างแบคทีเรียออกจากระบบทางเดินปัสสาวะ</p></li><li><p>- ปัสสาวะเมื่อรู้สึกต้องการ อย่ากลั้นปัสสาวะนาน</p></li><li><p>- รักษาความสะอาดบริเวณอวัยวะเพศ โดยเช็ดจากด้านหน้าไปด้านหลัง</p></li><li><p>- หลีกเลี่ยงการใช้สบู่หรือผลิตภัณฑ์ที่ระคายเคืองบริเวณอวัยวะเพศ</p></li><li><p>- สวมใส่เสื้อผ้าที่ระบายอากาศได้ดี</p></li><li><p>- หากมีอาการควรไปพบแพทย์เพื่อรับการตรวจและรักษาด้วยยาปฏิชีวนะอย่างเหมาะสม</p></li><li><p>- ในบางราย แพทย์อาจแนะนำให้ดื่มน้ำแครนเบอร์รีเพื่อช่วยป้องกันการติดเชื้อซ้ำ</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ใช้ถุงยางอนามัยทุกครั้งเมื่อมีเพศสัมพันธ์</p></li><li><p>- จำกัดจำนวนคู่รักทางเพศ และตรวจสุขภาพทางเพศอย่างสม่ำเสมอ</p></li><li><p>- หลีกเลี่ยงการใช้สารเสพติดที่อาจทำให้ความสามารถในการตัดสินใจลดลง</p></li><li><p>- หลีกเลี่ยงการมีเพศสัมพันธ์กับผู้ที่มีอาการติดเชื้อหรือมีประวัติโรคติดต่อทางเพศ</p></li><li><p>- หากสงสัยว่าติดเชื้อ ควรพบแพทย์เพื่อรับการตรวจและรักษาอย่างถูกต้อง</p></li><li><p>- แจ้งคู่รักให้ตรวจรักษาพร้อมกันเพื่อป้องกันการติดเชื้อซ้ำ</p></li><li><p>- รักษาความสะอาดของอวัยวะเพศและสุขอนามัยส่วนตัว</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- หลีกเลี่ยงอาหารที่มีกรดยูริกสูง เช่น เครื่องในสัตว์ เนื้อแดง อาหารทะเล หอย และเครื่องดื่มที่มีแอลกอฮอล์</p></li><li><p>- ดื่มน้ำมาก ๆ เพื่อช่วยขับกรดยูริกออกทางปัสสาวะ</p></li><li><p>- ควบคุมน้ำหนักให้อยู่ในเกณฑ์ปกติ</p></li><li><p>- ออกกำลังกายอย่างสม่ำเสมอและพักผ่อนเพียงพอ</p></li><li><p>- รับประทานยาแก้อักเสบหรือยาลดกรดยูริกตามคำแนะนำของแพทย์</p></li><li><p>- หลีกเลี่ยงการดื่มแอลกอฮอล์และเครื่องดื่มที่มีน้ำตาลสูง</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- ดื่มน้ำมาก ๆ และรับประทานอาหารที่มีกากใยสูง เช่น ผัก ผลไม้ เพื่อป้องกันท้องผูก</p></li><li><p>- ออกกำลังกายอย่างสม่ำเสมอเพื่อช่วยระบบขับถ่าย</p></li><li><p>- หลีกเลี่ยงการกลั้นอุจจาระหรือเบ่งถ่ายแรงเกินไป</p></li><li><p>- หลีกเลี่ยงการนั่งหรือนั่งยองนานเกินไป</p></li><li><p>- ใช้ยาทา หรือยาสอดที่แพทย์แนะนำเพื่อลดอาการอักเสบและบวม</p></li><li><p>- หากอาการรุนแรงควรพบแพทย์เพื่อรับการรักษาเพิ่มเติม เช่น การผ่าตัด</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- หลีกเลี่ยงการเกาจมูกแรง ๆ หรือใส่อะไรแหย่จมูก</p></li><li><p>- รักษาความชุ่มชื้นของโพรงจมูก เช่น ใช้น้ำเกลือล้างจมูก</p></li><li><p>- หลีกเลี่ยงสิ่งกระตุ้นที่ก่อให้เกิดภูมิแพ้ เช่น ฝุ่น ควัน และสารเคมี</p></li><li><p>- หลีกเลี่ยงการอยู่ในที่แห้ง หรือมีอากาศเย็นจัดเกินไป</p></li><li><p>- หากมีเลือดออกจากจมูก ให้บีบจมูกช่วงปลายประมาณ 5-10 นาทีและก้มหน้าไปข้างหน้าเพื่อป้องกันการกลืนเลือด</p></li><li><p>- หากเลือดไหลไม่หยุดหรือมีเลือดออกมาก ควรพบแพทย์ทันที</p></li><li><p>- รักษาโรคภูมิแพ้หรือการติดเชื้ออย่างเหมาะสมตามคำแนะนำแพทย์</p></li></ul>'),
('<p><strong>การปฐมพยาบาล :</strong></p><ul><li><p><strong>- ตรวจสอบความปลอดภัยของบริเวณเกิดเหตุ</strong> ก่อนเข้าไปช่วยเหลือ</p></li><li><p><strong>- ตรวจสอบสติและการหายใจ</strong> ของผู้บาดเจ็บ หากไม่หายใจให้ทำ CPR ทันที</p></li><li><p><strong>- หยุดเลือด</strong> โดยใช้ผ้าสะอาดกดปิดแผลอย่างเบา ๆ</p></li><li><p><strong>- ป้องกันการเคลื่อนไหวของคอและกระดูกสันหลัง</strong> โดยอย่าให้ผู้บาดเจ็บขยับตัวโดยไม่จำเป็น</p></li><li><p><strong>- ให้นอนราบและยกขาสูงเล็กน้อย</strong> หากไม่มีอาการบาดเจ็บที่ศีรษะหรือกระดูกสันหลัง เพื่อป้องกันช็อก</p></li><li><p><strong>- คลุมผู้บาดเจ็บด้วยผ้าห่ม</strong> เพื่อรักษาอุณหภูมิร่างกาย</p></li><li><p>- หากผู้บาดเจ็บมีบาดแผลเปิดให้ล้างแผลด้วยน้ำสะอาดและปิดด้วยผ้าสะอาด<br><br></p></li></ul><p><strong>การเคลื่อนย้ายผู้ป่วยในที่เกิดเหตุ :</strong></p><ul><li><p>- หากไม่มีความจำเป็นหรือไม่ได้รับการฝึกอบรม ไม่ควรเคลื่อนย้ายผู้บาดเจ็บโดยเฉพาะผู้ที่สงสัยว่ามีการบาดเจ็บที่กระดูกสันหลังหรือศีรษะ</p></li><li><p>- หากต้องเคลื่อนย้าย เช่น เพื่อความปลอดภัย (อัคคีภัย) ให้ใช้วิธี <strong>การเคลื่อนย้ายทั้งร่างกาย (spinal motion restriction)</strong> เช่น การใช้เสื่อหรือบอร์ดพยุงตัว</p></li><li><p>- ใช้วิธี <strong>ลากแบบสายพาน</strong> หรือ <strong>การจับพยุงตัวแบบสามคน</strong> หากไม่มีอุปกรณ์ช่วย</p></li><li><p>- ให้เคลื่อนย้ายอย่างระมัดระวัง ไม่บิดหรืองอคอและกระดูกสันหลัง</p></li><li><p>- รีบนำผู้บาดเจ็บส่งโรงพยาบาลโดยเร็ว</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><ul><li><p>- อย่าเคลื่อนย้ายผู้บาดเจ็บหากไม่จำเป็นโดยเฉพาะถ้ามีอาการบาดเจ็บที่ศีรษะหรือกระดูกสันหลัง</p></li><li><p>- หลีกเลี่ยงการบิดหรืองอร่างกายผู้บาดเจ็บ</p></li><li><p>- หลีกเลี่ยงการยกหรือขยับผู้บาดเจ็บอย่างรุนแรง</p></li><li><p>- ถ้าต้องเคลื่อนย้ายให้ใช้วิธีช่วยเหลือแบบถูกต้อง เช่น การพยุงด้วยบอร์ดหรือเสื่อพยุง</p></li><li><p>- รีบนำส่งโรงพยาบาลโดยเร็ว</p></li><li><p>- ในชีวิตประจำวันควรระมัดระวังการใช้อุปกรณ์ที่สูง เช่น บันได และสวมอุปกรณ์ป้องกันเมื่อทำงานในที่สูง</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><p><strong>การปฐมพยาบาลเบื้องต้น (หลัก R.I.C.E.):</strong></p><ul><li><p><strong>- Rest</strong>: หยุดใช้งานบริเวณที่บาดเจ็บ</p></li><li><p><strong>- Ice</strong>: ประคบเย็น 15-20 นาทีทุก 1-2 ชั่วโมง เพื่อลดบวม</p></li><li><p><strong>- Compression</strong>: ใช้ผ้ายืดพันเพื่อป้องกันอาการบวมมากขึ้น</p></li><li><p><strong>- Elevation</strong>: ยกบริเวณที่บาดเจ็บให้อยู่สูงกว่าระดับหัวใจ</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><p><strong>การดูแลเบื้องต้น:</strong></p><ul><li><p>- ลองดื่มน้ำ หรือน้ำลายกลืนตามปกติ หากเป็นก้างเล็ก อาจหลุดเอง</p></li><li><p>- รับประทานอาหารที่นุ่มแต่มีแรงดัน เช่น ข้าวปั้นกลืนตาม หรือกล้วย (ห้ามใช้วิธีนี้หากเจ็บมากหรือกลืนไม่ได้)</p></li><li><p>- หลีกเลี่ยงการแหย่ลำคอด้วยมือหรือของแข็ง เพราะอาจทำให้แผลลึกหรือก้างฝังมากขึ้น</p></li><li><p>- หากก้างยังไม่หลุด หรือรู้สึกเจ็บมาก <strong>ควรไปพบแพทย์ทันที</strong> เพื่อให้ผู้เชี่ยวชาญนำออกอย่างปลอดภัย<br></p></li></ul><p><strong>การป้องกัน:</strong></p><ul><li><p>- เลือกปลาที่มีก้างน้อย หรือเอาก้างออกก่อนรับประทาน</p></li><li><p>- เคี้ยวอาหารให้ละเอียดก่อนกลืน</p></li><li><p>- ระมัดระวังโดยเฉพาะในเด็กหรือผู้สูงอายุ</p></li><li><p>- หลีกเลี่ยงการพูดหรือหัวเราะขณะกินปลา</p></li></ul>'),
('<p><strong>วิธีดูแล และ การป้องกัน</strong></p><p><strong>การดูแลเบื้องต้น:</strong></p><ul><li><p>- ล้างแผลทันทีด้วยน้ำสะอาดหรือสบู่อ่อน ๆ อย่างน้อย 5 นาที</p></li><li><p>- ห้ามใช้แอลกอฮอล์หรือไฮโดรเจนเปอร์ออกไซด์โดยตรงกับแผลลึก เพราะอาจทำลายเนื้อเยื่อ</p></li><li><p>- กดแผลเพื่อห้ามเลือดด้วยผ้าสะอาดหรือผ้าก๊อซ</p></li><li><p>- ปิดแผลด้วยผ้าพันแผลที่สะอาด</p></li><li><p>- หากแผลลึกหรือเลือดไม่หยุด ควรรีบนำส่งโรงพยาบาล</p></li><li><p>- ควรพิจารณาฉีดวัคซีนบาดทะยักหากไม่ได้รับการกระตุ้นภายใน 5–10 ปี<br></p></li></ul><p><strong>การป้องกัน:</strong></p><ul><li><p>- ใช้ของมีคมอย่างระมัดระวัง และเก็บในที่ปลอดภัย</p></li><li><p>- สวมถุงมือป้องกันเมื่อต้องใช้เครื่องมือหรืออุปกรณ์แหลมคม</p></li><li><p>- ห้ามปล่อยเด็กเล่นของมีคมโดยเด็ดขาด</p></li><li><p>- หากของมีคมตกอยู่ ควรเก็บให้เรียบร้อยทันที</p></li></ul>');