INSERT INTO `situation` (`detail`) VALUES
('<h3><strong>การติดต่อ :</strong></h3><ul><li><p>ติดต่อได้ง่ายจากการสัมผัสผิวหนังของคนที่ติดเชื้อ</p></li><li><p>หรือสัมผัสสิ่งของที่มีเชื้อ เช่น ผ้า เสื้อผ้า ของใช้ส่วนตัว</p></li><li><p>สัตว์เลี้ยง เช่น แมวและสุนัขที่ติดเชื้อก็สามารถแพร่เชื้อได้</p></li></ul>'),
('<h3><strong>การติดต่อ :</strong></h3><ul><li><p>- ความดันโลหิตสูง <strong>ไม่ติดต่อ</strong> จากคนสู่คน</p></li></ul>'),
('<h3><strong>การติดต่อ :</strong></h3><ul><li><p>- งูสวัด <strong>ไม่ติดต่อโดยตรงเป็นงูสวัด</strong> แต่ไวรัสที่อยู่ในตุ่มน้ำสามารถติดต่อไปสู่คนที่ไม่เคยติดเชื้ออีสุกอีใสมาก่อน ทำให้เกิดโรคอีสุกอีใสแทน</p></li><li><p>- การติดต่อเกิดจากการสัมผัสกับน้ำในตุ่มน้ำของผู้ป่วยงูสวัด</p></li><li><p>- ผู้ป่วยงูสวัดควรหลีกเลี่ยงการสัมผัสกับผู้ที่ไม่เคยเป็นอีสุกอีใสมาก่อน หรือผู้ที่มีภูมิคุ้มกันต่ำ</p></li></ul>'),
('<h3><strong>การติดต่อ</strong></h3><ul><li><p><strong>ขึ้นอยู่กับสาเหตุของผื่น เช่น :</strong></p><ul><li><p>- หากเกิดจากการติดเชื้อ (เชื้อรา เชื้อแบคทีเรีย หรือไวรัส) อาจติดต่อได้ทางการสัมผัส</p></li><li><p>- หากเกิดจากการแพ้หรือโรคภูมิแพ้ จะไม่ติดต่อ</p></li></ul></li><li><p>- ควรตรวจสอบสาเหตุเพื่อประเมินการติดต่อได้อย่างถูกต้อง</p></li></ul>'),
('<h3><strong>การติดต่อ :</strong></h3><ul><li><p>- ฝีโดยทั่วไป <strong>ไม่ติดต่อจากคนสู่คนโดยตรง</strong></p></li><li><p>- แต่เชื้อแบคทีเรียที่ทำให้เกิดฝีสามารถแพร่กระจายได้หากสัมผัสกับหนองหรือสารคัดหลั่งจากฝี โดยเฉพาะถ้าไม่มีการป้องกัน</p></li><li><p>- ควรระวังการสัมผัสกับแผลเปิดและทำความสะอาดมือบ่อย ๆ</p></li></ul>'),
('<h3><strong>การติดต่อ :</strong></h3><ul><li><p>- ลมพิษ <strong>ไม่ติดต่อ</strong> จากคนสู่คน</p></li></ul>'),
('<p><strong>การติดต่อ :</strong></p><ul><li><p>- ติดต่อโดยการสัมผัสตรงกับตุ่มน้ำ หรือทางละอองฝอยจากการไอหรือจามของผู้ป่วย</p></li><li><p>- ติดต่อได้ง่ายและรวดเร็วในคนที่ไม่เคยเป็นหรือไม่เคยได้รับวัคซีน</p></li></ul>'),
('<p><strong>การติดต่อ :</strong></p><ul><li><p>- ติดต่อผ่านทางละอองฝอยจากการไอ จาม หรือการสัมผัสกับสิ่งของที่มีเชื้อไวรัส</p></li><li><p>- ติดต่อได้ง่ายในสถานที่แออัด</p></li></ul>'),
('<p><strong>การติดต่อ :</strong></p><ul><li><p>- หากแผลพุพองเกิดจากเชื้อแบคทีเรียหรือไวรัสบางชนิด อาจติดต่อผ่านการสัมผัสกับของเหลวในตุ่มน้ำ</p></li><li><p>- ควรหลีกเลี่ยงการสัมผัสกับแผลหรือของเหลวจากแผลของผู้อื่น</p></li></ul>'),
('<p><strong>การติดต่อ :</strong></p><ul><li><p>- ติดต่อผ่านการถูกยุงลายที่มีเชื้อไวรัสกัด</p></li><li><p>- ไม่ติดต่อโดยตรงจากคนสู่คน</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ตากุ้งยิงไม่ติดต่อจากคนสู่คนโดยตรง</p></li><li><p>- แต่แบคทีเรียที่ทำให้เกิดตากุ้งยิงสามารถแพร่ผ่านการสัมผัสมือสกปรกหรือการใช้ของส่วนตัวร่วมกันได้</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อผ่านการสัมผัสโดยตรงกับแผลหรือตุ่มน้ำของผู้ติดเชื้อ</p></li><li><p>- ติดต่อได้ทั้งทางการจูบ สัมผัสผิวหนัง หรือทางเพศสัมพันธ์</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้ไม่ติดต่อจากคนสู่คน</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อผ่านทางละอองฝอยจากการไอ จาม หรือการสัมผัสใกล้ชิดกับผู้ป่วย</p></li><li><p>- ติดต่อได้ง่ายในสถานที่แออัด เช่น โรงเรียน</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อผ่านทางละอองฝอยจากการไอ จาม</p></li><li><p>- สัมผัสกับน้ำลายหรือสารคัดหลั่งของผู้ป่วย เช่น การใช้แก้วน้ำหรือช้อนร่วมกัน</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้เองไม่ติดต่อโดยตรง</p></li><li><p>- แต่อาจเกิดตามหลังโรคติดเชื้อทางเดินหายใจส่วนบน เช่น หวัด หรือไข้หวัด ซึ่งสามารถติดต่อได้</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้ไม่ติดต่อโดยตรงจากคนสู่คน</p></li><li><p>- แต่แบคทีเรียในช่องปากสามารถถ่ายทอดได้ทางการใช้ของใช้ร่วมกัน เช่น แปรงสีฟัน หรือการจูบ</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคคอพอกไม่ใช่โรคติดต่อ</p></li><li><p>- แต่สาเหตุบางอย่างอาจพบในคนในครอบครัวเดียวกัน เช่น โรคภูมิคุ้มกันทำลายต่อมไทรอยด์</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้ไม่ใช่โรคติดต่อ</p></li><li><p>- แต่สามารถเกิดร่วมกับภาวะหรือโรคอื่น เช่น ภาวะขาดน้ำ หัวใจเต้นผิดจังหวะ หรือใช้ยาบางชนิด</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้ไม่ใช่โรคติดต่อ</p></li><li><p>- เป็นเพียงภาวะเลือดออกใต้เยื่อบุตาจากการกระทบกระเทือนหรือแรงดันในร่างกาย</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อได้ทางการรับประทานอาหารหรือดื่มน้ำที่ปนเปื้อนเชื้อโรค</p></li><li><p>- การสัมผัสกับอุจจาระของผู้ป่วย หรือของใช้ที่มีเชื้อปนเปื้อน</p></li><li><p>- พบมากในพื้นที่ที่สุขอนามัยไม่ดี</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อโดยการรับประทานอาหารหรือน้ำที่ปนเปื้อนเชื้อโรค</p></li><li><p>- ติดต่อจากการสัมผัสกับอุจจาระของผู้ติดเชื้อโดยตรง หรือของใช้ที่ปนเปื้อน</p></li><li><p>- ติดต่อได้ง่ายในสถานที่ที่แออัดหรือสุขาภิบาลไม่ดี เช่น โรงเรียน ศูนย์เด็กเล็ก ค่ายผู้อพยพ</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคใจสั่นใจหวิวไม่ใช่โรคติดต่อ</p></li><li><p>- เป็นอาการที่เกิดจากปัจจัยภายในร่างกายหรือภายนอก เช่น ความเครียด หรือโรคประจำตัว</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อโดยการรับประทานอาหารหรือน้ำที่ปนเปื้อนไข่หรือตัวอ่อนของพยาธิตัวตืด</p></li><li><p>- อาจเกิดจากการบริโภคเนื้อสัตว์ที่ปรุงไม่สุก เช่น เนื้อวัว เนื้อหมู หรือปลาที่มีตัวพยาธิ</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อโดยการกลืนไข่พยาธิที่ปนเปื้อนมากับมือ อาหาร ของเล่น หรือผ้าปูที่นอน</p></li><li><p>- การเกาแล้วนำมือเข้าปากทำให้เกิดการติดเชื้อซ้ำ (cycle of reinfection)</p></li><li><p>- ติดต่อกันง่ายในกลุ่มเด็กหรือในครอบครัว</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- โรคนี้ไม่ใช่โรคติดต่อทางเพศสัมพันธ์โดยตรง แต่การมีเพศสัมพันธ์อาจเพิ่มความเสี่ยงในการติดเชื้อ</p></li><li><p>- ติดต่อจากแบคทีเรียที่ปนเปื้อนในท่อปัสสาวะและแพร่เข้าสู่กระเพาะปัสสาวะ</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ติดต่อผ่านการมีเพศสัมพันธ์โดยไม่ป้องกันกับผู้ที่ติดเชื้อ</p></li><li><p>- การสัมผัสกับเลือด หรือของเหลวในร่างกายที่มีเชื้อ</p></li><li><p>- จากแม่สู่ลูกระหว่างการคลอดในบางโรค</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- เกาต์ไม่ใช่โรคติดต่อ</p></li><li><p>- เกิดจากภาวะภายในร่างกาย เช่น การสะสมกรดยูริกสูงจากการกินอาหารบางชนิด หรือปัญหาเมตาบอลิซึม</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ริดสีดวงทวารไม่ใช่โรคติดต่อ</p></li><li><p>- เกิดจากปัจจัยภายในร่างกายและพฤติกรรมการใช้ชีวิต</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- อาการถ่ายมูกและมูกเลือดไม่ใช่โรคติดต่อ แต่สาเหตุที่เป็นเชื้อไวรัสหรือแบคทีเรียที่ทำให้เกิดการติดเชื้อในจมูกอาจติดต่อกันได้</p></li><li><p>- ภูมิแพ้เองไม่ติดต่อ</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- อุบัติเหตุรถล้มไม่ใช่โรคติดต่อ</p></li><li><p>- แต่ควรระมัดระวังการติดเชื้อจากบาดแผล เช่น แผลเปิดหรือเลือดที่สัมผัสกับผู้ช่วยเหลือ</p></li><li><p>- ผู้ช่วยเหลือควรใช้ถุงมือหรืออุปกรณ์ป้องกันเพื่อลดความเสี่ยงการติดเชื้อจากสารคัดหลั่งหรือเลือด</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- เหตุการณ์ตกจากที่สูงไม่ใช่โรคติดต่อ</p></li><li><p>- แต่ควรระวังการติดเชื้อจากบาดแผล หากมีแผลเปิดหรือเลือดสัมผัสผู้ช่วยเหลือ</p></li><li><p>- ควรใช้ถุงมือหรืออุปกรณ์ป้องกันเมื่อต้องสัมผัสผู้บาดเจ็บ</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- การบาดเจ็บจากการเล่นกีฬาไม่ใช่โรคติดต่อ</p></li><li><p>- อย่างไรก็ตาม หากมีแผลเปิด ควรระมัดระวังการสัมผัสเลือดหรือน้ำเหลืองเพื่อลดความเสี่ยงการติดเชื้อ</p></li><li><p>- ผู้ช่วยเหลือควรใช้ถุงมือหากต้องสัมผัสแผลหรือเลือด</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- ก้างปลาติดคอไม่ใช่ภาวะติดต่อ</p></li><li><p>- แต่ถ้ามีแผลหรือเลือด ควรหลีกเลี่ยงการสัมผัสโดยตรงเพื่อป้องกันการติดเชื้อจากน้ำลายหรือเลือด</p></li></ul>'),
('<p><strong>การติดต่อ</strong></p><ul><li><p>- แผลจากของมีคม <strong>ไม่ใช่โรคติดต่อโดยตรง</strong></p></li><li><p>- แต่ควรระวังการสัมผัสเลือดหรือสารคัดหลั่งจากแผล เพราะอาจเป็นพาหะของโรคติดต่อทางเลือด เช่น ไวรัสตับอักเสบ B, C หรือ HIV</p></li><li><p>- ผู้ให้ความช่วยเหลือควรสวมถุงมือยางเสมอ</p></li></ul>');