INSERT INTO `symptom` (`detail`) VALUES
('<h3><strong>อาการ :</strong></h3><ul><li><p>มีผื่นแดงเป็นวงกลมขอบนูน ชัดเจน</p></li><li><p>ผื่นจะมีขอบเขตชัดเจนเป็นวงคล้ายวงแหวน (จึงเรียก Ringworm)</p></li><li><p>บางครั้งมีอาการคันร่วมด้วย</p></li><li><p>บางรายอาจมีสะเก็ดหรือผิวหนังลอกบริเวณแผล</p></li><li><p>สามารถเกิดได้ตามส่วนต่าง ๆ ของร่างกาย เช่น แขน ขา หนังศีรษะ หรือบริเวณขาหนีบ</p></li></ul>'),
('<h3><strong>อาการของโรค :</strong></h3><ul><li><p>- มักไม่มีอาการในช่วงแรก (โรคเงียบ)</p></li><li><p>- อาจมีอาการปวดศีรษะ เวียนศีรษะ หายใจลำบาก ใจสั่นในบางราย</p></li><li><p>- หากไม่ได้รับการรักษา อาจเกิดภาวะแทรกซ้อนรุนแรง เช่น หัวใจวาย หลอดเลือดสมองแตก</p></li></ul>'),
('<h3><strong>อาการของโรค :</strong></h3><ul><li><p>- ปวดแสบ ปวดร้อน หรือรู้สึกชาตามแนวเส้นประสาทที่ไวรัสกลับมา</p></li><li><p>- ผื่นแดงขึ้นเป็นกลุ่ม แล้วพัฒนาเป็นตุ่มน้ำใส (พุพอง)</p></li><li><p>- ตุ่มน้ำแตกแล้วตกสะเก็ด</p></li><li><p>- ผื่นมักขึ้นตามด้านใดด้านหนึ่งของร่างกาย เช่น หน้าอก หลัง หรือใบหน้า</p></li><li><p>- อาจมีไข้ ปวดศีรษะ อ่อนเพลียร่วมด้วย</p></li></ul>'),
('<h3><strong>อาการของโรค</strong></h3><ul><li><p>- มีผื่นแดงขึ้นตามผิวหนัง บางครั้งเป็นตุ่มน้ำ ตุ่มหนอง หรือลอกเป็นสะเก็ด</p></li><li><p>- รู้สึกคันมากจนต้องเกา</p></li><li><p>- ผิวหนังอาจบวม หรือมีรอยถลอกจากการเกา</p></li><li><p>- อาการอาจเป็นเฉพาะที่หรือทั่วตัว</p></li><li><p>- อาจมีอาการร่วม เช่น แสบร้อน หรือเจ็บเล็กน้อย</p></li></ul>'),
('<h3><strong>อาการของโรค :</strong></h3><ul><li><p>- ก้อนนูน บวม แดง และเจ็บปวดบริเวณผิวหนัง</p></li><li><p>- บางครั้งมีผิวหนังบริเวณฝีรอบ ๆ ร้อนและบวมมากขึ้น</p></li><li><p>- อาจมีหนองสะสมใต้ผิวหนัง และบางครั้งฝีจะเปิดออกแล้วมีหนองไหลออกมา</p></li><li><p>- อาจมีไข้ หนาวสั่น หรืออ่อนเพลีย หากการติดเชื้อรุนแรง</p></li><li><p>- อาการเจ็บจะเพิ่มขึ้นเมื่อกดหรือเคลื่อนไหวบริเวณนั้น</p></li></ul>'),
('<h3><strong>อาการของโรค :</strong></h3><ul><li><p>- มีตุ่มนูนแดงหรือผื่นขึ้นบนผิวหนังอย่างรวดเร็ว</p></li><li><p>- มีอาการคันมาก บางครั้งรู้สึกแสบร้อนหรือเจ็บ</p></li><li><p>- ผื่นอาจขึ้นเป็นวงหรือแผ่ขยายไปทั่วร่างกาย</p></li><li><p>- อาการมักหายไปภายใน 24 ชั่วโมง แต่มักเกิดขึ้นซ้ำได้</p></li></ul>'),
('<p><strong>อาการของโรค :</strong></p><ul><li><p>- มีไข้ต่ำ ๆ หรือสูงก่อนเกิดผื่น</p></li><li><p>- ผื่นเริ่มจากตุ่มแดงเล็ก ๆ แล้วพัฒนาเป็นตุ่มน้ำใส</p></li><li><p>- ตุ่มน้ำแตกและตกสะเก็ดภายในไม่กี่วัน</p></li><li><p>- คันมากและมักขึ้นทั่วตัว รวมถึงใบหน้าและหนังศีรษะ</p></li><li><p>- อาจมีอาการเหนื่อย เพลียร่วมด้วย</p></li></ul>'),
('<p><strong>อาการของโรค :</strong></p><ul><li><p>- ไข้สูงเฉียบพลัน หนาวสั่น</p></li><li><p>- ปวดศีรษะ ปวดเมื่อยกล้ามเนื้อ</p></li><li><p>- ไอแห้ง เจ็บคอ น้ำมูกไหล</p></li><li><p>- อ่อนเพลีย เหนื่อยง่าย</p></li><li><p>- บางรายอาจมีอาการคลื่นไส้ อาเจียน</p></li></ul>'),
('<p><strong>อาการของโรค :</strong></p><ul><li><p>- มีตุ่มน้ำหรือพุพองที่ผิวหนัง บางครั้งเจ็บหรือแสบร้อน</p></li><li><p>- พุพองอาจแตกและมีของเหลวไหลออกมา</p></li><li><p>- บริเวณรอบแผลอาจแดง บวม และเจ็บ</p></li><li><p>- อาจมีหนองหรือกลิ่นไม่พึงประสงค์ถ้าเกิดการติดเชื้อ</p></li><li><p>- อาจมีไข้ร่วมในกรณีที่ติดเชื้อรุนแรง</p></li></ul>'),
('<p><strong>อาการของโรค :</strong></p><ul><li><p>- มีไข้สูงอย่างรวดเร็วประมาณ 2-7 วัน</p></li><li><p>- ปวดศีรษะอย่างรุนแรง</p></li><li><p>- ปวดข้อและกล้ามเนื้อ</p></li><li><p>- มีจุดเลือดออกหรือผื่นแดงตามผิวหนัง</p></li><li><p>- เลือดออกตามเหงือก จมูก หรือมีเลือดออกในระบบทางเดินอาหารในรายที่รุนแรง</p></li><li><p>- อ่อนเพลียมาก</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- มีตุ่มนูนแดง เจ็บและบวมที่ขอบเปลือกตา</p></li><li><p>- รู้สึกระคายเคืองหรือแสบตา</p></li><li><p>- บางครั้งมีน้ำตาไหลหรือรู้สึกหนักตา</p></li><li><p>- ตุ่มอาจแตกและมีหนองไหลออกมาในระยะต่อมา</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- มีตุ่มน้ำใสเล็ก ๆ รวมกันเป็นกลุ่ม บริเวณปาก ใบหน้า หรืออวัยวะเพศ</p></li><li><p>- ตุ่มน้ำจะแตกเป็นแผลเจ็บและตกสะเก็ดภายใน 1-2 สัปดาห์</p></li><li><p>- อาจมีอาการปวด แสบ หรือคันบริเวณที่เป็น</p></li><li><p>- ในบางรายอาจมีไข้ ปวดเมื่อยกล้ามเนื้อ และต่อมน้ำเหลืองบวมร่วมด้วย</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- คัดจมูก แน่นจมูก หายใจลำบาก</p></li><li><p>- น้ำมูกใสไหลตลอดเวลา</p></li><li><p>- จามบ่อย และมีอาการคันจมูก คอ และตา</p></li><li><p>- ตาแดงหรือแสบตาในบางราย</p></li><li><p>- บางครั้งอาจมีอาการปวดหัวหรือรู้สึกเมื่อยล้า</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- มีไข้ต่ำถึงปานกลาง</p></li><li><p>- ปวดและบวมบริเวณใต้กรามหรือแก้มด้านใดด้านหนึ่งหรือทั้งสองข้าง</p></li><li><p>- เจ็บเวลากลืนน้ำลายหรือเคี้ยวอาหาร</p></li><li><p>- บางรายอาจมีอาการปวดศีรษะ อ่อนเพลีย และเบื่ออาหารร่วมด้วย</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- เจ็บคอ โดยเฉพาะเวลากลืนอาหาร</p></li><li><p>- คอบวมแดง มีจุดหนองสีขาวหรือเหลืองที่ต่อมทอนซิล</p></li><li><p>- มีไข้ หนาวสั่น</p></li><li><p>- มีกลิ่นปาก</p></li><li><p>- เสียงเปลี่ยนหรือพูดไม่ชัดเพราะคอบวม</p></li><li><p>- ต่อมน้ำเหลืองบริเวณคอบวมและเจ็บ</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- คัดจมูกและมีน้ำมูกข้นเหนียว อาจมีสีเขียวหรือเหลือง</p></li><li><p>- ปวดหรือกดเจ็บบริเวณหน้าผาก โหนกแก้ม รอบดวงตา หรือฐานจมูก</p></li><li><p>- ปวดศีรษะ โดยเฉพาะเมื่อลุกขึ้นนั่งหรือก้มศีรษะ</p></li><li><p>- มีไข้ (ในบางราย)</p></li><li><p>- กลิ่นปากไม่พึงประสงค์</p></li><li><p>- อาจมีอาการไอ โดยเฉพาะเวลากลางคืน</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- เหงือกบวม แดง และเจ็บ</p></li><li><p>- มีเลือดออกขณะแปรงฟันหรือใช้ไหมขัดฟัน</p></li><li><p>- มีกลิ่นปาก</p></li><li><p>- เหงือกร่นหรือแยกตัวออกจากฟัน</p></li><li><p>- ในบางรายอาจมีอาการเสียวฟันร่วมด้วย</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- มีก้อนหรือต่อมนูนบริเวณด้านหน้าลำคอ อาจโตขึ้นเรื่อย ๆ</p></li><li><p>- รู้สึกแน่นคอหรือหายใจลำบาก (ในรายที่ก้อนโตมาก)</p></li><li><p>- เสียงแหบหรือกลืนอาหารลำบาก</p></li><li><p>- อาจมีอาการร่วมของภาวะไทรอยด์ทำงานผิดปกติ เช่น</p><ul><li><p><strong>ไทรอยด์ทำงานเกิน</strong>: ใจสั่น เหงื่อออก น้ำหนักลด ขี้ร้อน</p></li><li><p><strong>ไทรอยด์ทำงานน้อย</strong>: อ่อนเพลีย น้ำหนักขึ้น หนาวง่าย</p></li></ul></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- เวียนศีรษะ หรือรู้สึกโคลงเคลงเมื่อเปลี่ยนอิริยาบถ เช่น ลุกขึ้นยืน</p></li><li><p>- หน้ามืด ตาพร่า หรือเห็นดาว</p></li><li><p>- เหนื่อยง่าย อ่อนเพลีย</p></li><li><p>- หัวใจเต้นเร็วหรือผิดจังหวะ</p></li><li><p>- เป็นลมหรือหมดสติในบางกรณี</p></li><li><p>- ผิวหนังเย็น ชื้น และซีด</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- เห็นเป็นจุดหรือปื้นแดงบนตาขาว (ไม่เจ็บ ไม่เคืองตา)</p></li><li><p>- ไม่มีอาการตามัวหรือปวดตา</p></li><li><p>- ตาอาจรู้สึกตึงหรือรำคาญเล็กน้อยในบางราย</p></li><li><p>- ไม่มีน้ำตาไหลหรือตาแดงแบบอักเสบทั่วไป</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- ถ่ายอุจจาระเหลวหรือเป็นน้ำ</p></li><li><p>- ปวดท้องหรือปวดบิด</p></li><li><p>- คลื่นไส้ อาเจียน</p></li><li><p>- อ่อนเพลีย</p></li><li><p>- อาจมีไข้ หรืออุจจาระมีมูกเลือดในบางกรณี</p></li><li><p>- หากรุนแรง อาจมีอาการขาดน้ำ เช่น ปากแห้ง ปัสสาวะน้อย หน้ามืด</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- ถ่ายเหลว มีมูกหรือเลือดปนในอุจจาระ</p></li><li><p>- ปวดเบ่งถ่าย (tenesmus) หรือรู้สึกอยากถ่ายบ่อย ๆ แม้ไม่มีอุจจาระ</p></li><li><p>- ปวดท้องหรือปวดบิด</p></li><li><p>- มีไข้ หนาวสั่น</p></li><li><p>- คลื่นไส้ อาเจียน</p></li><li><p>- หากรุนแรง อาจทำให้เกิดภาวะขาดน้ำหรือช็อกได้</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- รู้สึกหัวใจเต้นเร็ว หรือเต้นแรงกว่าปกติ</p></li><li><p>- หัวใจเต้นไม่สม่ำเสมอ หรือเต้นกระตุก</p></li><li><p>- รู้สึกใจหวิวหรือวูบวาบในอก</p></li><li><p>- บางรายอาจมีอาการเวียนศีรษะ หรือเป็นลมวูบร่วมด้วย</p></li><li><p>- อาจรู้สึกเหนื่อยหรือหายใจลำบาก</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- ปวดท้อง ท้องอืด หรือรู้สึกไม่สบายท้อง</p></li><li><p>- เบื่ออาหาร น้ำหนักลดโดยไม่ทราบสาเหตุ</p></li><li><p>- คลื่นไส้ อาเจียน</p></li><li><p>- อุจจาระอาจพบปล้องพยาธิหรือไข่พยาธิ</p></li><li><p>- อ่อนเพลียหรือมีอาการซีดจากภาวะโลหิตจางในบางราย</p></li><li><p>- ในบางกรณีอาจไม่มีอาการชัดเจน</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- คันรุนแรงบริเวณทวารหนัก โดยเฉพาะในตอนกลางคืน</p></li><li><p>- นอนไม่หลับหรือหลับไม่สนิท</p></li><li><p>- อาจมีอาการระคายเคืองบริเวณทวารหนัก หรือพบผื่นแดง</p></li><li><p>- อาจมีอาการปวดท้องเบา ๆ หรือเบื่ออาหารในบางราย</p></li><li><p>- ในเด็กอาจพบการหงุดหงิดหรือกังวลใจ</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- ปัสสาวะขัด ปัสสาวะบ่อยและเร่งด่วน</p></li><li><p>- ปัสสาวะมีกลิ่นเหม็นหรือขุ่น</p></li><li><p>- ปวดหรือแสบขณะปัสสาวะ</p></li><li><p>- ปวดท้องน้อยหรือรู้สึกแน่นบริเวณกระเพาะปัสสาวะ</p></li><li><p>- อาจมีไข้ต่ำ ๆ หรือหนาวสั่นในบางราย</p></li><li><p>- ในบางกรณีอาจมีเลือดปนในปัสสาวะ</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- มีอาการตกขาวหรือของเหลวจากอวัยวะเพศที่ผิดปกติ</p></li><li><p>- ปวดหรือแสบขณะปัสสาวะ</p></li><li><p>- มีแผลหรือผื่นที่อวัยวะเพศหรือบริเวณรอบ ๆ</p></li><li><p>- บางรายอาจมีอาการเจ็บปวดบริเวณอวัยวะเพศหรือท้องน้อย</p></li><li><p>- อาการอาจไม่แสดงในบางคน แต่ยังสามารถแพร่เชื้อได้</p></li><li><p>- อาการอื่น ๆ เช่น ไข้ ต่อมน้ำเหลืองโต หรือรู้สึกไม่สบายทั่วไป</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- ปวดข้ออย่างรุนแรงและเฉียบพลัน โดยเฉพาะข้อหัวแม่เท้า</p></li><li><p>- ข้อบวม แดง และร้อนบริเวณที่ปวด</p></li><li><p>- มีอาการปวดมากในช่วงกลางคืนหรือหลังทานอาหารที่มีกรดยูริกสูง</p></li><li><p>- อาจมีไข้ร่วมด้วยในบางราย</p></li><li><p>- ในบางกรณีอาจเกิดก้อนแข็ง (โทฟัส) ตามข้อหรือเนื้อเยื่อรอบ ๆ</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- มีเลือดสดปนกับอุจจาระหรือหยดหลังถ่าย</p></li><li><p>- รู้สึกเจ็บหรือคันบริเวณทวารหนัก</p></li><li><p>- มีตุ่มบวมหรือก้อนบริเวณทวารหนัก</p></li><li><p>- อาจรู้สึกไม่สบายเวลานั่งหรือเดิน</p></li><li><p>- ในกรณีรุนแรงอาจมีอาการปวดมากและบวมอย่างชัดเจน</p></li></ul>'),
('<p><strong>อาการของโรค</strong></p><ul><li><p>- น้ำมูกไหล อาจเป็นน้ำมูกใส หรือข้นเป็นหนอง</p></li><li><p>- มีเลือดออกจากจมูกเล็กน้อยหรือมาก</p></li><li><p>- คัดจมูกและจามบ่อย</p></li><li><p>- เจ็บหรือระคายเคืองภายในโพรงจมูก</p></li><li><p>- อาจมีอาการอื่นร่วม เช่น ปวดหัว หรือปวดใบหน้า</p></li></ul>'),
('<p><strong>อาการของผู้บาดเจ็บ</strong></p><ul><li><p>- มีแผลเปิดหรือเลือดออก</p></li><li><p>- ปวดหรือบาดเจ็บที่ศีรษะ คอ หรือหลัง</p></li><li><p>- ไม่รู้สึกตัวหรือรู้สึกสับสน</p></li><li><p>- หายใจลำบากหรือหายใจผิดปกติ</p></li><li><p>- แขนขาเคลื่อนไหวไม่ได้ หรือมีอาการชา</p></li><li><p>- ช็อก (ผิวซีด เหงื่อออก ตัวเย็น)</p></li></ul>'),
('<p><strong>อาการของผู้บาดเจ็บ</strong></p><ul><li><p>- ปวดหรือบาดเจ็บที่ศีรษะ คอ หรือหลัง</p></li><li><p>- มีแผลเปิดหรือเลือดออก</p></li><li><p>- ไม่รู้สึกตัว หรือสับสน</p></li><li><p>- หายใจลำบาก หรือหายใจผิดปกติ</p></li><li><p>- แขนขาเคลื่อนไหวไม่ได้ หรือชาร่วมด้วย</p></li><li><p>- มีอาการช็อก เช่น ตัวซีด เหงื่อออก ตัวเย็น</p></li></ul>'),
('<p><strong>อาการของผู้บาดเจ็บ</strong></p><ul><li><p>- ปวด บวม แดง หรือฟกช้ำบริเวณที่ได้รับบาดเจ็บ</p></li><li><p>- ข้อเคลื่อนไม่ได้ หรือเคลื่อนไหวลำบาก</p></li><li><p>- รู้สึกอ่อนแรงหรือชา</p></li><li><p>- มีเสียง \"เป๊าะ\" หรือ \"ดังกร๊อบ\" ขณะบาดเจ็บ</p></li><li><p>- หากบาดเจ็บรุนแรง เช่น กระดูกหักหรือศีรษะกระแทก อาจหมดสติหรือมีอาการคลื่นไส้อาเจียนร่วมด้วย</p></li></ul>'),
('<p><strong>อาการของผู้บาดเจ็บ</strong></p><ul><li><p>- เจ็บคอหรือระคายเคืองในลำคอ</p></li><li><p>- รู้สึกเหมือนมีสิ่งแปลกปลอมติดอยู่</p></li><li><p>- กลืนลำบากหรือเจ็บขณะกลืน</p></li><li><p>- อาจมีเลือดออกเล็กน้อยในบางกรณี</p></li><li><p>- หากปล่อยไว้นาน อาจเกิดบวม อักเสบ หรือเป็นไข้ได้</p></li></ul>'),
('<p><strong>อาการของผู้บาดเจ็บ</strong></p><ul><li><p>- มีแผลเปิดหรือแผลลึก</p></li><li><p>- เลือดออกจากแผลมากหรือน้อย ขึ้นอยู่กับความลึก</p></li><li><p>- อาจรู้สึกปวดหรือแสบ</p></li><li><p>- หากแผลลึกอาจเห็นกล้ามเนื้อหรือเนื้อเยื่อภายใน</p></li><li><p>- อาจมีอาการบวม แดง หรือมีหนอง หากติดเชื้อ</p></li></ul>');